import React from "react";
import {Link} from 'react-router-dom'
import apiconfig from '../config/api.config.json'

class Header extends React.Component {

  componentWillReceiveProps(newProps) {
    this.setState({
        formdata : newProps.username
    })
}

  onSignOut () {
    localStorage.clear();
    //this.Props.history.push('/')
}

  render() {
    let userdata = JSON.parse(localStorage.getItem(apiconfig.LS.USERDATA))
    return (
      <nav class="navbar navbar-expand-lg navbar-light bg-dark">
        <a class="navbar-brand text-white ml-4" href="/home">Hi Engineer</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
        <div class = "container">
  <div class="collapse navbar-collapse" id="navbarSupportedContent">
    <ul class="navbar-nav mr-auto">
      <li class="nav-item dropdown">
        <a class="nav-link dropdown-toggle text-white" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Catalog
        </a>
        <div class="dropdown-menu bg-dark" aria-labelledby="navbarDropdown">
          <a class="dropdown-item" href="#">T-shirt</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#">Shoes</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#">Bag</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#">Hat</a>
        </div>
      </li>
    </ul>
    <ul className="navbar-nav ml-auto">
      <div className="mr-2">
    <div class="dropdown">
      <button class="btn bg-transparent text-white dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
      hi, {userdata[0].username}
      </button>
      <div class="dropdown-menu bg-dark" aria-labelledby="dropdownMenuButton">
        <a class="dropdown-item text-white" href="#">Profil</a>
        <div class="dropdown-divider"></div>
        <Link class="dropdown-item nav-link" to = "" onClick = {this.onSignOut}>
        <a>Keluar</a>
        </Link>
      </div>
    </div>
      </div>
     </ul>
  </div>
  </div>
</nav>


    );
  }
}

export default Header;
