import React from 'react'
import {Modal, ModalBody, ModalFooter, ModalHeader, Button} from 'reactstrap'
import { MDBRow, MDBCol, MDBBtn } from "mdbreact"
import axios from 'axios'
import Select from "react-select";
import apiconfig from '../../../config/api.config.json'

class ViewMahasiswa extends React.Component {
    constructor (props) {
        super (props)
        let userdata = JSON.parse(localStorage.getItem(apiconfig.LS.USERDATA))
        this.state ={
            provinsi: [],
            kota: [],
            listKota: [],
            listProvinsi: [],
            selectedOption:{},
            selectedOption2:{},
            selectedValue: "",
            selectedValue2: "",
            productsListNew : []
            
        }

        this.handleChange1 = this.handleChange1.bind(this)
        this.handleChange2 = this.handleChange2.bind(this)
    }

    handleChange1 = selectedOption => {
        this.setState({ selectedValue: selectedOption.value });
        let tmp = this.state.formdata
        tmp["kode_provinsi"] = selectedOption.value
    };
    handleChange2 = selectedOption => {
        this.setState({ selectedValue2: selectedOption.value });
        let tmp = this.state.formdata
        tmp["kode_kota"] = selectedOption.value
    };

    handleChange1 = (selectedOption) => {
        this.setState({selectedOption});
      };
    
    handleChange2 = (selectedOption) => {
        this.setState({selectedOption2: selectedOption })
      }
    
      getProvinsi() {
        let token = localStorage.getItem(apiconfig.LS.TOKEN);
        let option = {
            url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.PROVINSI,
            method: "GET",
            headers: {
                "Authorization": token
            }
        };
        axios(option)
            .then(response => {
                let tmp = [];

                response.data.message.map(row => {
                    tmp.push({
                        value: row.kode_provinsi,
                        label: row.nama_provinsi
                    });
                });
                this.setState({
                    listProvinsi: tmp,
                    provinsi: response.data.message
                });
            })
            .catch(error => {
                alert(error);
            });
    }

    getKota() {
        let token = localStorage.getItem(apiconfig.LS.TOKEN);
        let option = {
            url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.KOTA,
            method: "GET",
            headers: {
                "Authorization": token
            }
        };
        axios(option)
            .then(response => {
                let tmp = [];

                response.data.message.map(row => {
                    tmp.push({
                        value: row.kode_kota,
                        label: row.nama_kota,
                        link: row.kode_provinsi
                    });
                });
                this.setState({
                    listKota: tmp,
                    kota: response.data.message
                });

            })
            .catch(error => {
                alert(error);
            });
    }    
    componentDidMount() {
        this.getProvinsi();
        this.getKota();
      }

    handleOnChange(values) {
        this.setState({ values });
        }

    changeHandler(e) {
        let tmp = this.state.formdata
        tmp [e.target.name] = e.target.value
        this.setState({
            formdata : tmp
        })
    }
    render () {
        const options1 = this.state.listProvinsi;
        const options2 =this.state.listKota;

        const filteredOptions = options2.filter(
        o => o.link === this.state.selectedOption.value)
        return (
            <Modal isOpen = {this.props.view} className = {this.props.className}>
                <ModalHeader>View Unit</ModalHeader>
                <ModalBody>
                    
                <form>
                  <div class="row">
                    <div class="col">
                      <label for="kode_mahasiswa">Kode Mahasiswa</label>
                            <input
                                type="text"
                                class="form-control"
                                name="kode_mahasiswa"
                                value={this.props.listmahasiswa.kode_mahasiswa}
                            />
                        </div>
                          <div class="col">
                          <label for="exampleInputEmail1">Nama Mahasiswa</label>
                            <input
                                type="text"
                                class="form-control"
                                name="nama_mahasiswa"
                                value={this.props.listmahasiswa.nama_mahasiswa}
                            />
                        </div>
                      </div>
                    </form>

                            <div class="form-group">
                            <label for="exampleInputEmail1">Alamat</label>
                            <input
                                type="text"
                                class="form-control"
                                name="alamat"
                                value={this.props.listmahasiswa.alamat}
                                onChange={this.changeHandler}
                            />
                        </div>

                        <form>
                  <div class="row">
                    <div class="col">
                    <label for="text"> Pilih Provinsi  </label>
                        <Select
                        name="kode_provinsi"
                        value={options1.filter(({ value }) => value === this.props.listmahasiswa.kode_provinsi)}
                        onChange={this.handleChange1}
                        options={options1}
                        required
                        />
                        </div>

                        <div class="col">
                        <label for="text"> Kota  </label>
                        <Select type="text" class="form-control" placeholder="Pilih kota"
                        name="kode_kota"
                        value={options2.filter(({ value }) => value === this.props.listmahasiswa.kode_kota)}
                        onChange={this.handleChange2}
                        options={filteredOptions}
                        required
                        />
                    </div>
                </div>
            </form>

            <form>
                  <div class="row">
                    <div class="col">
                      <label for="kode_mahasiswa">Kode Agama</label>
                            <input
                                type="text"
                                class="form-control"
                                name="kode_agama"
                                value={this.props.listmahasiswa.kode_agama}
                            />
                        </div>
                          <div class="col">
                          <label for="exampleInputEmail1">Kode Jurusan</label>
                            <input
                                type="text"
                                class="form-control"
                                name="kode_jurusan"
                                value={this.props.listmahasiswa.kode_jurusan}
                            />
                        </div>
                      </div>
                    </form>


            <form>
                  <div class="row">
                    <div class="col">
                      <label for="kode_mahasiswa">Kode kota</label>
                            <input
                                type="text"
                                class="form-control"
                                name="kode_kota"
                                value={this.props.listmahasiswa.kode_kota}
                            />
                        </div>
                          <div class="col">
                          <label for="exampleInputEmail1">Kode Provinsi</label>
                            <input
                                type="text"
                                class="form-control"
                                name="kode_provinsi"
                                value={this.props.listmahasiswa.kode_provinsi}
                            />
                        </div>
                      </div>
                    </form>

                        <div class="form-group">
            <label for="kode jurusan">Jenis Kelamin</label>
            </div>

                    <div class="form-group">
                        <div class="custom-control custom-radio custom-control-inline">
                            <input type="radio" id='jenis_kelamin1' name = 'jenis_kelamin'
                            value='Laki-laki'
                            checked={this.props.listmahasiswa.jenis_kelamin === 'Laki-laki'}
                            class='custom-control-input' required />
                            <label class='custom-control-label' for ='jenis_kelamin1'>Laki-laki</label>
                            </div>

                            <div class="custom-control custom-radio custom-control-inline">
                            <input type="radio" id='jenis_kelamin2' name = 'jenis_kelamin'
                            value='Perempuan'
                            checked={this.props.listmahasiswa.jenis_kelamin === 'Perempuan'}
                            class='custom-control-input' required />
                            <label class='custom-control-label' for ='jenis_kelamin2'>Perempuan</label>
                            </div>
                    </div>

                    <div class="form-group">
                            <label for="kode jurusan">Hobi</label>
                            <input
                                type="text"
                                class="form-control"
                                name="kode_jurusan"
                                readOnly
                                value={this.props.listmahasiswa.hobi}
                            />
                            </div>

                </ModalBody>
                <ModalFooter>
                    <Button color = "danger" onClick = {this.props.modalStatus}>Close</Button>
                </ModalFooter>
            </Modal>
        )
    }
}

export default ViewMahasiswa
